CREATE TABLE "educate" (
"id" INTEGER PRIMARY KEY NOT NULL DEFAULT NULL,
"educateThumbURL" TEXT DEFAULT NULL,
"educatePDFURL" TEXT DEFAULT NULL,
"sectionID" INTEGER);
CREATE TABLE "evaluate" (
"id" INTEGER PRIMARY KEY NOT NULL DEFAULT NULL,
"documentTitle" TEXT DEFAULT NULL,
"documentPDFURL" TEXT DEFAULT NULL,
"sectionID" TEXT DEFAULT NULL);
CREATE TABLE "explore" (
"id" INTEGER PRIMARY KEY NOT NULL DEFAULT NULL,
"productTitle" TEXT DEFAULT NULL,
"productDesc" TEXT DEFAULT NULL,
"productThumbURL" TEXT DEFAULT NULL,
"productBackgroundImageURL" TEXT DEFAULT NULL,
"sectionID" INTEGER);
CREATE TABLE "interact" (
"id" INTEGER PRIMARY KEY NOT NULL DEFAULT NULL,
"videoTitle" TEXT DEFAULT NULL,
"videoThumbURL" TEXT DEFAULT NULL,
"videoURL" TEXT DEFAULT NULL,
"sectionID" INTEGER DEFAULT NULL,
"isInternal" BLOB NOT NULL);
CREATE TABLE "sections" (
"id" INTEGER PRIMARY KEY NOT NULL DEFAULT NULL,
"name" TEXT DEFAULT NULL,
"folderName" TEXT DEFAULT NULL,
"description" TEXT DEFAULT NULL,
"mainBackground" TEXT DEFAULT NULL,
"darkBackground" TEXT DEFAULT NULL,
"lightBackground" TEXT DEFAULT NULL);
CREATE TABLE "visualize" (
"id" INTEGER PRIMARY KEY NOT NULL DEFAULT NULL,
"imageTitle" TEXT DEFAULT NULL,
"imageThumbURL" TEXT DEFAULT NULL,
"imageLargeURL" TEXT DEFAULT NULL,
"sectionID" INTEGER);
